contracts/CourseManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CourseManager is Ownable {
    struct Course {
        uint256 id;
        address author;
        string title;
        string content;
        uint256 priceYD; // price denominated in YD (18 decimals)
        bool active;
    }

    IERC20 public immutable yd;
    uint256 public exchangeRateEthPerYD; // mock rate, amount of wei per 1 YD (1e18)
    uint256 public nextId = 1;

    // courseId => Course
    mapping(uint256 => Course) public courses;
    // user => courseId => purchased
    mapping(address => mapping(uint256 => bool)) public purchased;

    event CourseCreated(uint256 indexed id, address indexed author, string title, uint256 priceYD);
    event CoursePurchased(uint256 indexed id, address indexed buyer, uint256 priceYD);
    event RateUpdated(uint256 weiPerYD);

    constructor(address ydToken) Ownable(msg.sender) {
        yd = IERC20(ydToken);
        exchangeRateEthPerYD = 1e15; // default 0.001 ETH per 1 YD (for mock ETH purchases)
    }

    function setEthPerYDRate(uint256 weiPerYD) external onlyOwner {
        exchangeRateEthPerYD = weiPerYD;
        emit RateUpdated(weiPerYD);
    }

    function createCourse(string calldata title, string calldata content, uint256 priceYD) external returns (uint256) {
        require(priceYD > 0, "price zero");
        uint256 id = nextId++;
        courses[id] = Course({
            id: id,
            author: msg.sender,
            title: title,
            content: content,
            priceYD: priceYD,
            active: true
        });
        emit CourseCreated(id, msg.sender, title, priceYD);
        return id;
    }

    function setCourseActive(uint256 id, bool active) external {
        Course storage c = courses[id];
        require(msg.sender == c.author || msg.sender == owner(), "not auth");
        c.active = active;
    }

    // ================== YD兑换功能 ==================
    function buyYD() external payable {
        require(msg.value > 0, "Send ETH");
        uint256 amount = msg.value * 1e18/exchangeRateEthPerYD;
        require(yd.balanceOf(address(this)) >= amount, "Not enough YD in pool");
        yd.transfer(msg.sender, amount);
    }

    // Buy with YD token (needs approval)
    function buyWithYD(uint256 id) external {
        Course memory c = courses[id];
        require(c.active, "inactive");
        require(!purchased[msg.sender][id], "already bought");
        require(yd.transferFrom(msg.sender, c.author, c.priceYD), "transfer fail");
        purchased[msg.sender][id] = true;
        emit CoursePurchased(id, msg.sender, c.priceYD);
    }

    // Convenience: buy with ETH using mock rate; ETH is sent directly to author
    function buyWithETH(uint256 id) external payable {
        Course memory c = courses[id];
        require(c.active, "inactive");
        require(!purchased[msg.sender][id], "already bought");
        uint256 need = c.priceYD * exchangeRateEthPerYD / 1e18;
        require(msg.value >= need, "insufficient eth");
        purchased[msg.sender][id] = true;
        (bool ok, ) = payable(c.author).call{value: need}("");
        require(ok, "pay fail");
        // refund excess
        if (msg.value > need) {
            (bool ok2, ) = payable(msg.sender).call{value: msg.value - need}("");
            require(ok2, "refund fail");
        }
        emit CoursePurchased(id, msg.sender, c.priceYD);
    }

    // Helper views
    function isPurchased(address user, uint256 id) external view returns (bool) {
        return purchased[user][id];
    }

    function getCourse(uint256 id) external view returns (Course memory) {
        return courses[id];
    }
}

